mapper.select:
       SELECT MIN(SEQ) AS SEQ 
    	 FROM LOG_${sHogi}DCC_ALARM 
    	 
    	 	
	        	AND ALMXYGUBUN = #{sXYGubun}
	        
	        
	        	AND ALMDATE >=  #{sDate)}
	        	        
            
	            AND SEQ >=  #{iDate)}
	        	       
	        
	        	AND SEQ >  #{sPageNo)}
	        	        
            
	            AND PAGNO >=  #{iPageNo)}

    	SELECT MAX(SEQ) AS SEQ 
    	 FROM LOG_${sHogi}DCC_ALARM 
    	 
    	 	
	        	AND ALMXYGUBUN = #{sPageNo}
	        
	        
	        	AND SEQ <  #{sPageNo)}
	        	        
            
	            AND PAGNO <=  #{iPageNo)}

SELECT ALMGUBUN, substring(ALMDATE,12,15) AS ALMTIME , ALMMSECCHK, ALMCODE, ALMADDRESS, ALMMESG, CONVERT(CHAR(10), ALMDATE,111) ALMYEAR , ALMMESGLINE, CONVERT(CHAR(23),ALMDATE,21) AS ALMDATE, PAGNO, PAGLINE, SEQ FROM LOG_${sHogi}DCC_ALARM
AND ALMXYGUBUN = #{sXYGubun}
AND SEQ = #{nSeq)}
ORDER BY PAGLINE

SELECT
	COUNT(*)
FROM LOG_${hogi}DCC_ALARM

	AND SEQ >= Trim(Replace(Left(#{startDate},7),'-',''))
	AND ALMHOGI = #{hogi}
	AND ALMXYGUBUN = #{xyGubun}
	AND CONVERT(VARCHAR(16), ALMDATE, 120) BETWEEN #{startDate} AND #{endDate}
	
		AND ALMGUBUN = #{alarmGubun}
	
	
		AND ALMCODE = #{alarmCode}
	
	
		AND ISNUMERIC(ALMADDRESS) = 1
		AND CONVERT(INT, ALMADDRESS) IN ( #{address} )
	
	
		AND ALMMESG LIKE '%${alarmMsg}%'

select.if:
SELECT MIN(SEQ) AS SEQ FROM LOG_${hogi}DCC_ALARM
WHERE ALMXYGUBUN = #{xyGubun} AND ALMDATE >= #{startDate}
AND SEQ >= Trim(Replace(Left(#{startDate},7),'-',''))

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS rowNum, ALMGUBUN, ALMCODE, ALMADDRESS, CONVERT(VARCHAR(23), ALMDATE, 21) AS ALMDATE, ALMMESG, ALMMSECCHK, SEQ, ALMHOGI, ALMXYGUBUN, ALMMESGLINE, PAGNO, PAGLINE, '' AS ALMYEAR FROM LOG_${hogi}DCC_ALARM
AND SEQ >= Trim(Replace(Left(#{startDate},7),'-','')) AND ALMHOGI = #{hogi} AND ALMXYGUBUN = #{xyGubun} AND CONVERT(VARCHAR(16), ALMDATE, 120) BETWEEN #{startDate} AND #{endDate}
AND ALMGUBUN = #{alarmGubun}
AND ALMCODE = #{alarmCode}
AND ISNUMERIC(ALMADDRESS) = 1 AND CONVERT(INT, ALMADDRESS) IN ( #{address} )
AND ALMMESG LIKE '%${alarmMsg}%'
ORDER BY ALMDATE DESC, PAGLINE DESC OFFSET (#{pageNum} - 1) * #{pageSize} ROWS FETCH NEXT #{pageSize} ROWS ONLY

SELECT ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS rowNum, ALMGUBUN, ALMCODE, ALMADDRESS, CONVERT(VARCHAR(23), ALMDATE, 21) AS ALMDATE, ALMMESG, ALMMSECCHK, SEQ, ALMHOGI, ALMXYGUBUN, ALMMESGLINE, PAGNO, PAGLINE, '' AS ALMYEAR FROM LOG_${hogi}DCC_ALARM
AND SEQ >= Trim(Replace(Left(#{startDate},7),'-','')) AND ALMHOGI = #{hogi} AND ALMXYGUBUN = #{xyGubun} AND CONVERT(VARCHAR(16), ALMDATE, 120) BETWEEN #{startDate} AND #{endDate}
AND ALMGUBUN = #{alarmGubun}
AND ALMCODE = #{alarmCode}
AND ISNUMERIC(ALMADDRESS) = 1 AND CONVERT(INT, ALMADDRESS) IN ( #{address} )
AND ALMMESG LIKE '%${alarmMsg}%'
ORDER BY ALMDATE ASC

SELECT ALMGUBUN, CASE ALMMSECCHK WHEN 0 THEN LEFT(CONVERT(CHAR(23), ALMDATE,114),8) ELSE LEFT(CONVERT(CHAR(23), ALMDATE,114),8)+'.'+IIF(LEN(RIGHT(CONVERT(CHAR(23), ALMDATE,114),3)) = 0,'000',RIGHT(CONVERT(CHAR(23), ALMDATE,114),3)) END AS ALMTIME, ALMMSECCHK, ALMCODE, ALMADDRESS, ALMMESG, CONVERT(CHAR(23), ALMDATE,111) ALMYEAR, ALMMESGLINE, CASE ALMMSECCHK WHEN 0 THEN LEFT(CONVERT(CHAR(23), ALMDATE,21),19) ELSE LEFT(CONVERT(CHAR(23), ALMDATE,21),19)+'.'+IIF(LEN(RIGHT(CONVERT(CHAR(23), ALMDATE,21),3)) = 0,'000',RIGHT(CONVERT(CHAR(23), ALMDATE,21),3)) END AS ALMDATE, PAGNO, PAGLINE, SEQ FROM LOG_${hogi}DCC_SUMMARY
AND ALMXYGUBUN = #{xyGubun} AND SEQ = #{seq}
ORDER BY PAGLINE

select
	address,
	loopName,
	eHigh,
	eLow,
	iSeq,
	FASTIOCHK
from MST_TAG_DCC

	AND ihogi= #{sHogi}
	AND xygubun= #{sXYGubun}
	AND iotype = #{ioType}
	AND address = #{address}
	
		AND IOBIT = #{ioBit}

DELETE FROM MST_GrpTag_B

	AND GrpHOGI = #{sHogi}
	AND ID = #{sGrpID}
	AND MenuNo = #{sMenuNo}
	AND GrpNo = #{sUGrpNo}

SELECT iSeq FROM MST_TAG_DCC

	AND IHOGI = #{hogiMod}
	
		AND XYGUBUN = 'Y'
	
	
		AND XYGUBUN = 'X'
	
		AND IOTYPE = #{ioTypeMod}
		AND ADDRESS = #{addressMod}
	
		AND IOBIT = #{ioBitMod}

SELECT iSeq, iHogi, xyGubun, loopName, descr, ioType, address, ioBit, eLow, eHigh, '0' as saveCore FROM MST_TAG_DCC WHERE IHOGI = #{tagHogi}
AND IOTYPE = #{tagIOType}
AND IOTYPE <> 'CI' AND (ADDRESS like '%${findData}%'
OR LOOPNAME like '%${findData}%'
OR DESCR like '%${findData}%'
) ORDER BY ISEQ

SELECT

	TOP 2 


	TOP 1 

	CONVERT(CHAR(23), ALMDATE,121) AS ALMDATE,
	ALMADDRESS,
	ALMGUBUN
FROM LOG_${sHogi}DCC_ALARM

	
		AND SEQ >= Trim(Replace(Left(#{startDate},7),'-',''))
	
	
		AND CONVERT(VARCHAR(23), ALMDATE, 121) > #{startDate}
	
	
		AND CONVERT(VARCHAR(23), ALMDATE, 121) BETWEEN #{startDate} AND #{endDate}
	
		AND ALMHOGI = #{sHogi}
		AND ALMXYGUBUN = #{sXYGubun}
	
		AND ALMGUBUN = #{alarmGubun}
	
		AND ALMCODE = 'CI'
		AND ISNUMERIC(ALMADDRESS) = 1
	
		AND CONVERT(INT,ALMADDRESS) = #{address}
	
	
		AND CONVERT(INT,ALMADDRESS) in (${address})
	


	ORDER BY ALMDATE DESC


	ORDER BY ALMDATE ASC

