mapper.select:
SELECT C.iSeq , BSCAL , LOOPNAME , UNIT , LIMIT1 , LIMIT2 , TYPE , IOTYPE , ADDRESS , IOBIT , C.TBLNO , FLDNO , SaveCoreChk , C.Descr as Descr , B.Descr as DataLoop , GrpHogi , ROUND(B.MinVal, 2) as MinVal , ROUND(B.MaxVal, 2) as MaxVal , Hogi , XYGubun , ELOW , EHIGH , VLOW , VHIGH , FASTIOCHK FROM MST_GrpTag B, MST_TAG_DCC C WHERE B.HOGI = C.IHOGI
AND B.ISEQ = C.ISEQ
AND B.YSEQ = C.ISEQ
AND B.GrpHOGI = #{hogi}
AND B.Gubun = #{dive}
AND B.ID = #{grpID}
AND B.MenuNo = #{menuNo}
AND B.GrpNo = #{uGrpNo}
ORDER BY B.TagNo

	SELECT FLDNO 
	 FROM MST_TAG_DCC 
    WHERE IHOGI = #{Hogi}
       AND ADDRESS = #{ADDRESS}
       AND IOTYPE IN ('FTAI','FTDT')

	SELECT Convert(varchar,ScanTime,120) as SCANTIME
	 FROM LOG_Timer
    WHERE GUBUN = 'D'
       AND HOGI = #{hogi}
        AND DATATYPE = #{xyGubun}

        	SELECT C.TBLNO, C.FLDNO 
        	 FROM MST_GRPTAG B, MST_TAG_DCC C 
        	WHERE B.HOGI = C.IHOGI
			
           			AND B.ISEQ = C.ISEQ
           	
            
           			AND B.YSEQ = C.ISEQ
           	  
            
            		AND B.GrpHOGI = #{hogi}
            
         	
           			AND B.Gubun = #{dive}
       		
       		
           			AND B.ID = #{grpID}
       		
       		
           			AND B.MenuNo = #{menuNo}
       		      
       		
           			AND B.GrpNo = #{uGrpNo}
       				                          		
        	ORDER BY B.TAGNO

        SELECT * 
         FROM LOG_${hogi}DCC_TREND
    	 WHERE Scantime = #{pSCanTime}
    	  AND Seq in (
    						SELECT C.TBLNO 
    						 FROM MST_GrpTag B, MST_TAG_DCC C
     					   WHERE B.HOGI = C.IHOGI 
     					     
		           					AND B.ISEQ = C.ISEQ
				           	 
     					     
		           					AND B.YSEQ = C.ISEQ
				           	  
				            
				            		AND B.GrpHOGI = #{hogi}
				            
				         	
				           			AND B.Gubun = #{dive}
				       		
				       		
				           			AND B.ID = #{grpID}
				       		
				       		
				           			AND B.MenuNo = #{menuNo}
				       		      
				       		
				           			AND B.GrpNo = #{uGrpNo}
				       			
				       		GROUP BY TBLNO	             
     					)

      	SELECT ${tblnoGrp} SEQ, * 
      	 FROM LOG_${hogi}DCC_TREND${tblnoGrp}
      	  
	      	 WHERE Scantime >  DATEADD(SECOND, -5,#{pSCanTime}) AND Scantime <= #{pSCanTime}
      	 
      	 
	      	 WHERE Scantime = #{pSCanTime}

SELECT b.Iseq iSeq, b.gubun, b.hogi, c.xyGubun, b.Descr, c.ioType, c.address, c.ioBit, b.minVal, b.maxVal, b.saveCoreChk FROM MST_GrpTag_B B, MST_TAG_DCC C
AND B.HOGI = C.IHOGI AND B.ISEQ = C.ISEQ
AND B.ID = #{grpID}
AND B.GrpHOGI = #{hogi}
AND B.MenuNo = #{menuNo}
AND B.GrpNo = #{uGrpNo}
AND B.Gubun = 'D'
SELECT b.Iseq iSeq_M, b.gubun gubun_M, b.hogi hogi_M, '' xyGubun_M, b.Descr Descr_M, c.iotype ioType_M, c.register register_M, c.iobit ioBit_M, b.minval minVal_M, b.maxval maxVal_M, '' saveCoreChk_M FROM mst_grptag_b b, markdb.dbo.mst_tag_mark c
AND B.HOGI = C.HOGI AND B.ISEQ = C.ISEQ
AND B.GrpHOGI = #{grpID}
AND B.ID = #{hogi}
AND B.MenuNo = #{menuNo}
AND B.GrpNo = #{uGrpNo}
AND B.Gubun = 'M'
ORDER BY B.TagNo

SELECT b.Iseq iSeq, b.gubun, b.hogi, c.xyGubun, b.Descr, c.ioType, c.address, c.ioBit, b.minVal, b.maxVal, b.saveCoreChk FROM MST_GrpTag B, MST_TAG_DCC C
AND B.HOGI = C.IHOGI AND B.ISEQ = C.ISEQ
AND B.ID = #{grpID}
AND B.GrpHOGI = #{hogi}
AND B.MenuNo = #{menuNo}
AND B.GrpNo = #{uGrpNo}
AND B.Gubun = 'D'
SELECT b.Iseq iSeq_M, b.gubun gubun_M, b.hogi hogi_M, '' xyGubun_M, b.Descr Descr_M, c.iotype ioType_M, c.register register_M, c.iobit ioBit_M, b.minval minVal_M, b.maxval maxVal_M, '' saveCoreChk_M FROM mst_grptag_b b, markdb.dbo.mst_tag_mark c
AND B.HOGI = C.HOGI AND B.ISEQ = C.ISEQ
AND B.GrpHOGI = #{grpID}
AND B.ID = #{hogi}
AND B.MenuNo = #{menuNo}
AND B.GrpNo = #{uGrpNo}
AND B.Gubun = 'M'
ORDER BY B.TagNo
