mapper.insert:
        INSERT INTO  MST_NOTICE (
			      TITLE
			      , CONTENTS
			      , CreateDate
			      , ID
			      , PWD
		  ) VALUES( 
		       #{title} 
		      , #{contents}
		      , GETDATE()
		      , #{id}
		      , #{pwd}
		     )

mapper.update:
        UPDATE MST_NOTICE 
        	  SET 
			       TITLE  = #{title}
			      , CONTENTS =  #{contents}
			      , CreateDate = GETDATE()
		  WHERE SEQNO = #{seqNo}
		     AND ID =  #{id}
		     AND PWD = #{pwd}

mapper.delete:
        DELETE FROM MST_NOTICE
        WHERE SEQNO  = #{seqNo}
		   AND ID =  #{id}
		   AND PWD = #{pwd}
