mapper.update:
          UPDATE MST_GrpName 
        	    SET UGrpName = #{uGrpName}
	       WHERE Gubun =#{gubun}
	       	AND ID =#{id}
	      	AND MenuNo = #{menuNo}
			AND UGrpNo = #{uGrpNo}

mapper.select:
    
        	SELECT Min(TVALUE${fldno})
        			, Max(TVALUE${fldno})
                    , AVG(TVALUE${fldno})
                    , STDEV(TVALUE${fldno})
                   
		                   	FROM LOG_3DCC_TREND${tlbno}
                    
                    
                    	FROM LOG_${hogi}DCC_TREND${tlbno}
                    
                   WHERE SCANTIME BETWEEN #{mskSpareS}  AND #{mskSpareE}


    	SELECT Min(TVALUE${fldno})
    			, Max(TVALUE${fldno})
                , AVG(TVALUE${fldno})
                , STDEV(TVALUE${fldno})
                
                	FROM LOG_4DCC_TREND${tlbno}
                
                
                	FROM LOG_${hogi}DCC_TREND${tlbno}
                
               WHERE SCANTIME BETWEEN #{mskFixedS}  AND #{mskFixedE}

        	SELECT * 
        	 FROM MST_TAG_DCC 
           
			        
			            AND IHOGI = #{sHogi}
			        
			        
			            AND IOTYPE = #{sIOType}
			        
			        AND IOTYPE <> 'CI'
			        
			            AND (ADDRESS LIKE '%' + #{findData} + '%'
			          
			        
			            OR LOOPNAME LIKE '%' + #{findData} + '%'
			          
			        
			            OR DESCR LIKE '%' + #{findData} + '%'
			              
			        )		        	        
		                
                ORDER BY ISEQ

mapper.delete:
 
  		DELETE FROM MST_GRPTAG
 		WHERE Gubun =#{gubun}
            AND GrpHOGI = #{grpHOGI}
			   AND ID = #{id}
            AND MenuNo = #{menuNo}
 		   AND GrpNo =#{grpNo}

mapper.select:
        	SELECT ISEQ
        	 FROM MST_TAG_DCC 
			WHERE IHOGI = #{iHogi}
			    AND XYGUBUN = #{xyGubun}
			    AND IOTYPE = #{ioType}
			    AND ADDRESS = #{address}
			   
			            AND IOBIT = #{ioBit}

	SELECT Convert(varchar,ScanTime,120) as SCANTIME
	 FROM LOG_Timer
    WHERE GUBUN = 'D'
       AND HOGI = #{sHogi}
        AND DATATYPE = #{sXYGubun}
