mapper.update:
UPDATE MST_USER
SET
 	 LOGIN = 'Y'
 	 , USERIP = #{userIp}
 	 , CONNTIME = GETDATE()
 	 , LOGINHOGI = #{hogi}
WHERE ID = #{userId}

mapper.select:
    	SELECT
			Convert(varchar,ScanTime,120) AS scanTime
		FROM LOG_Timer
    	WHERE Gubun = 'D'
		AND Hogi = #{hogi}
		AND DataType = #{xyGubun}

SELECT
	C.TBLNO,
	C.FLDNO
FROM MST_GRPTAG B, MST_TAG_DCC C

	AND B.HOGI = C.IHOGI
	
		AND B.ISEQ = C.ISEQ
	
	
		AND B.YSEQ = C.ISEQ
	
	AND B.Gubun = 'D'
	AND B.GrpHogi = #{hogi}
	AND B.ID = #{grpId}
	AND B.MenuNo = #{menuNo}
	AND B.GrpNo = #{grpNo}

ORDER BY B.TAGNO

SELECT IHOGI, XYGUBUN, DESCR, IOTYPE, ADDRESS, IOBIT, ISEQ, LOOPNAME FROM MST_TAG_DCC WHERE IHOGI = #{txtHogi} AND ADDRESS = #{txtAddress}
AND IOTYPE = #{txtIoType}
AND IOBIT = #{txtIoBit}

    	SELECT
			CONVERT(VARCHAR(10),ISNULL(a.ISEQ,''))+'_'+CONVERT(VARCHAR(10),ISNULL(b.ISEQ,'')) AS SEQS
		FROM MST_TAG_DCC a, MST_TAG_DCC b
		WHERE a.IHOGI = b.IHOGI
		AND a.IOTYPE = b.IOTYPE
		AND a.ADDRESS = b.ADDRESS
		
		AND a.IOBIT = b.IOBIT
		
		AND a.IHOGI = #{txtHogi}
		AND a.IOTYPE = #{txtIoType}
		AND a.ADDRESS = #{txtAddress}
		
		AND a.IOBIT = #{txtIoBit}
		
		AND a.XYGUBUN = 'X'
		AND b.XYGUBUN = 'Y'

mapper.update:
    	UPDATE MST_GRPTAG SET
		Hogi = #{txtHogi}
		
			,ISEQ = #{iSeq}
			,YSEQ = #{ySeq}
		
		
			
				,ISEQ = #{iSeq}
			
			
				,YSEQ = #{ySeq}
			
		
		WHERE GUBUN = #{gubun}
		AND ID = #{grpId}
		AND MENUNO = #{menuNo}
		AND GRPNO = #{grpNo}
		AND GrpHOGI = #{hogi}
		AND TAGNO = #{tagNo}
