select.if:
SELECT ISNULL(a.ADDRESS,'') AS ADDRESS, ISNULL(b.DESCR,'') AS DESCR, ISNULL(b.REV,'') AS REV, ISNULL(b.DRAWING,'') AS DRAWING, ISNULL(b.LOOPNAME,'') AS LOOPNAME, ISNULL(b.XYGUBUN,'') AS XYGUBUN, ISNULL(b.DEVICE,'') AS DEVICE, ISNULL(b.MESSAGE,'') AS MESSAGE, ISNULL(b.PURPOSE,'') AS PURPOSE, ISNULL(b.PROGRAM,'') AS PROGRAM, ISNULL(b.VLOW,'') AS VLOW, ISNULL(b.VHIGH,'') AS VHIGH, ISNULL(b.ELOW,'') AS ELOW, ISNULL(b.EHIGH,'') AS EHIGH, ISNULL(b.UNIT,'') AS UNIT, ISNULL(b.RTD,'') AS RTD, ISNULL(b.WINDOW,'') AS WINDOW, CASE ISNULL(b.CR,'') WHEN 0 THEN 'None' WHEN 1 THEN 'Conditioned' ELSE '' END AS CR, ISNULL(b.LIMIT1,'') AS LIMIT1, ISNULL(b.LIMIT2,'') AS LIMIT2, ISNULL(b.J,'') AS J, CASE ISNULL(b.N,'') WHEN 0 THEN 'Commission' WHEN 1 THEN 'Not Commission' ELSE '' END AS N, ISNULL(b.EQU#,'') AS EQU, ISNULL(b.BSCAL,'') AS BSCAL, CASE ISNULL(RTRIM(LTRIM(STR(b.WIBA))),'') WHEN 0 THEN 'OUT' WHEN 1 THEN 'IN' ELSE '' END AS WIBA, ISNULL(b.PRIORITY,'') AS PRIORITY, ISNULL(b.ZTEXT1,'') AS ZTEXT1, ISNULL(b.ZTEXT2,'') AS ZTEXT2, ISNULL(b.ZTEXT3,'') AS ZTEXT3, ISNULL(b.CONV,'') AS CONV, CASE ISNULL(b.TYPE,'') WHEN 0 THEN 'None' WHEN 1 THEN 'High' WHEN 2 THEN 'Low' WHEN 3 THEN 'High/Low' WHEN 4 THEN 'High DTAB' WHEN 5 THEN 'Low DTAB' WHEN 6 THEN 'High/Low DTAB' WHEN 7 THEN 'High/Very High' WHEN 8 THEN 'Low/Very Low' WHEN 9 THEN 'Irrational' ELSE '' END AS TYPE, CASE ISNULL(b.IOGROUP,'') WHEN 0 THEN 'None' WHEN 1 THEN 'Reactor and PHT' WHEN 2 THEN 'Turbine and boilers' WHEN 3 THEN 'Safety' WHEN 4 THEN 'Electrical' WHEN 8 THEN 'Auxiliaries' ELSE '' END AS IOGROUP, CASE ISNULL(b.PRIORITY,'') WHEN 0 THEN 'Printer only' WHEN 1 THEN 'Minor' WHEN 2 THEN 'Safety' WHEN 3 THEN 'Major' ELSE '' END AS PRIORITY FROM MST_TAG_DCC a, MST_TAG_DCC b WHERE ISNULL(a.IOTYPE,'') = 'FTAI' AND ISNULL(b.IOTYPE,'') = 'AI'
SELECT ISNULL(a.ADDRESS,'') AS ADDRESS, ISNULL(b.PROGRAM,'') AS PROGRAM, ISNULL(b.DESCR,'') AS DESCR, ISNULL(b.LOOPNAME,'') AS LOOPNAME FROM MST_TAG_DCC a, MST_TAG_DCC b WHERE ISNULL(a.IOTYPE,'') = 'FTDT' AND ISNULL(b.IOTYPE,'') = 'DT'
AND ISNULL(b.IHOGI,'') = a.IHOGI AND ISNULL(b.XYGUBUN,'') = a.XYGUBUN AND ISNULL(b.ADDRESS,'') = a.ADDRESS AND ISNULL(b.IHOGI,'') = #{iHogi} AND ISNULL(b.XYGUBUN,'') = #{xyGubun}
AND b.address in ( ${address} )
AND b.address between #{address} AND #{address2}
AND b.address = #{address}
AND b.${searchKey_1} LIKE '%${searchWord_1}%'
AND b.${searchKey_2} LIKE '%${searchWord_2}%'
AND b.${searchKey_3} LIKE '%${searchWord_3}$'
ORDER BY CONVERT(INT,b.ADDRESS)

