select.if:
SELECT ISNULL(a.ADDRESS,'') AS ADDRESS, ISNULL(b.DESCR,'') AS DESCR, ISNULL(b.REV,'') AS REV, ISNULL(b.DRAWING,'') AS DRAWING, ISNULL(b.LOOPNAME,'') AS LOOPNAME, ISNULL(b.XYGUBUN,'') AS XYGUBUN, ISNULL(b.DEVICE,'') AS DEVICE, ISNULL(b.MESSAGE,'') AS MESSAGE, ISNULL(b.VLOW,'') AS VLOW, ISNULL(b.VHIGH,'') AS VHIGH, ISNULL(b.ELOW,'') AS ELOW, ISNULL(b.EHIGH,'') AS EHIGH, ISNULL(b.CONV,'') AS CONV, ISNULL(b.TYPE,'') AS TYPE, ISNULL(b.IOGROUP,'') AS IOGROUP, ISNULL(b.PRIORITY,'') AS PRIORITY FROM MST_TAG_DCC a, MST_TAG_DCC b WHERE ISNULL(a.IOTYPE,'') = 'FTAI' AND ISNULL(b.IOTYPE,'') = 'AI'
SELECT ISNULL(a.ADDRESS,'') AS ADDRESS, ISNULL(b.PROGRAM,'') AS PROGRAM, ISNULL(b.DESCR,'') AS DESCR, ISNULL(b.LOOPNAME,'') AS LOOPNAME FROM MST_TAG_DCC a, MST_TAG_DCC b WHERE ISNULL(a.IOTYPE,'') = 'FTDT' AND ISNULL(b.IOTYPE,'') = 'DT'
AND ISNULL(b.IHOGI,'') = a.IHOGI AND ISNULL(b.XYGUBUN,'') = a.XYGUBUN AND ISNULL(b.ADDRESS,'') = a.ADDRESS AND ISNULL(b.IHOGI,'') = #{iHogi} AND ISNULL(b.XYGUBUN,'') = #{xyGubun}
AND b.address in ( ${address} )
AND b.address between #{address} AND #{address2}
AND b.address = #{address}
AND b.${searchKey_1} LIKE '%${searchWord_1}%'
AND b.${searchKey_2} LIKE '%${searchWord_2}%'
AND b.${searchKey_3} LIKE '%${searchWord_3}$'
ORDER BY CONVERT(INT,b.ADDRESS)

SELECT c.* FROM ( SELECT ISNULL(ADDRESS,'') AS ADDRESS, ISNULL(DESCR,'') AS DESCR, ISNULL(REV,'') AS REV, ISNULL(DRAWING,'') AS DRAWING, ISNULL(LOOPNAME,'') AS LOOPNAME, ISNULL(XYGUBUN,'') AS XYGUBUN, ISNULL(DEVICE,'') AS DEVICE, ISNULL(MESSAGE,'') AS MESSAGE, ISNULL(VLOW,'') AS VLOW, ISNULL(VHIGH,'') AS VHIGH, ISNULL(ELOW,'') AS ELOW, ISNULL(EHIGH,'') AS EHIGH, ISNULL(CONV,'') AS CONV, ISNULL(TYPE,'') AS TYPE, ISNULL(IOGROUP,'') AS IOGROUP, ISNULL(PRIORITY,'') AS PRIORITY, ISNULL(EQU#,'') AS EQU, ISNULL(BSCAL,'') AS BSCAL, ISNULL(WINDOW,'') AS WINDOW, ISNULL(WIBA,'') AS WIBA, ISNULL(UNIT,'') AS UNIT, ISNULL(LIMIT1,'') AS LIMIT2, ISNULL(LIMIT2,'') AS LIMIT1, ISNULL(PURPOSE,'') AS PURPOSE, ISNULL(PROGRAM,'') AS PROGRAM, ISNULL(RTD,'') AS RTD, ISNULL(J,'') AS J, ISNULL(ISEQ,'') AS ISEQ, ISNULL(IHOGI,'') AS IHOGI, ISNULL(IOTYPE,'') AS IOTYPE, ISNULL(ZTEXT1,'') AS ZTEXT1, ISNULL(ZTEXT2,'') AS ZTEXT2, ISNULL(ZTEXT3,'') AS ZTEXT3, ISNULL(TR,'') AS TR, ISNULL(CR,'') AS CR, ISNULL(IOBIT,'') AS IOBIT, ISNULL(COM1,'') AS COM1, ISNULL(COM2,'') AS COM2, ISNULL(N,'') AS N, ISNULL(CONDITION,'') AS CONDITION, ISNULL(CTRLNAME,'') AS CTRLNAME, ISNULL(INTERLOCK,'') AS INTERLOCK, ISNULL(FEEDBACK,'') AS FEEDBACK, ISNULL(ALARMCOND,'') AS ALARMCOND, ISNULL(INDICATE,'') AS INDICATE FROM MST_TAG_DCC WITH (NOLOCK)
AND iHogi = #{iHogi}
AND iHogi = 3
AND xyGubun = #{xyGubun}
AND xyGubun = 'X'
AND ioType = #{ioType}
AND address in ( ${address} )
AND address between #{address} AND #{address2}
AND address = #{address}
AND ${searchKey_1} LIKE '%${searchWord_1}%'
AND ${searchKey_2} LIKE '%${searchWord_2}%'
AND ${searchKey_3} LIKE '%${searchWord_3}%'
) c ORDER BY CONVERT(INT,ADDRESS)
