mapper.select:
SELECT * FROM 

	MST_GRPNAME_B


	MST_GRPNAME


	
		AND Gubun = #{sDive}
	
	AND ID = #{sGrpID}
	AND MenuNo = #{sMenuNo}

Select
	Convert(char(19),Scantime,120) AS SCANTIME,
	TVALUE${rs2} AS TVALUE
from log_${rs0}dcc_trend
where scantime = (
	select
		scantime
	from log_timer
	where gubun = 'D'
	and Hogi = #{rs0}
	and DataType = #{rs3}
)
and SEQ = #{rs1}

SELECT SCANTIME FROM LOG_Timer


	AND GUBUN = 'D'


	AND GUBUN = #{sDive}

	AND HOGI = #{sHogi}
	AND DATATYPE = #{sXYGubun}

    	SELECT
			TVALUE${FldNo}
		
			FROM LOG_${hogi}DCC_TREND
		
		
			FROM LOG_${hogi}DCC_TREND_HIST
		
		
			FROM LOG_${hogi}DCC_TREND_FAST
		
		WHERE SCANTIME = #{startDate}
		AND SEQ = #{seq}

mapper.delete:
 
  		DELETE FROM MST_GRPTAG
 		WHERE Gubun =#{gubun}
            AND GrpHOGI = #{grpHOGI}
			   AND ID = #{id}
            AND MenuNo = #{menuNo}
 		   AND GrpNo =#{grpNo}

mapper.select:
        	SELECT ISEQ
        	 FROM MST_TAG_DCC 
			WHERE IHOGI = #{iHogi}
			    AND XYGUBUN = #{xyGubun}
			    AND IOTYPE = #{ioType}
			    AND ADDRESS = #{address}
			   
			            AND IOBIT = #{ioBit}

select
	address,
	loopName,
	eHigh,
	eLow,
	iSeq,
	FASTIOCHK
from MST_TAG_DCC

	AND ihogi= #{sHogi}
	AND xygubun= #{sXYGubun}
	AND iotype = #{ioType}
	AND address = #{address}
	
		AND IOBIT = #{ioBit}
