mapper.select:
        SELECT
	         Gubun
	         , ID
	         , MenuNo
	         , UGrpNo
	         , UGrpName
        FROM MST_GrpName
        
	        
	            AND Gubun = #{dive}
	        
	        
	            AND ID = #{grpID}
	        
	        
	            AND MenuNo = #{menuNo}
	              
	        
	            AND UGrpNo = #{uGrpNo}

        SELECT
	       MAX(UGrpNo) as MAX_UGrpNo
        FROM MST_GrpName
        
	        
	            AND Gubun = #{dive}
	        
	        
	            AND ID = #{grpID}
	        
	        
	            AND MenuNo = #{menuNo}

SELECT
	MAX(UGrpNo) as MAX_UGrpNo
FROM MST_GrpName_B

	
		AND ID = #{grpID}
	
	
		AND MenuNo = #{menuNo}

SELECT UGrpName FROM MST_GrpName_B

	
		AND ID = #{grpID}
	
	
		AND MenuNo = #{menuNo}
	
	
		AND UGrpNo = #{uGrpNo}
