mapper.select:
SELECT C.iSeq , BSCAL , LOOPNAME , UNIT , LIMIT1 , LIMIT2 , TYPE , IOTYPE , ADDRESS , IOBIT , C.TBLNO , FLDNO , SaveCoreChk , C.Descr , B.Descr as DataLoop , GrpHogi , B.MinVal , B.MaxVal , Hogi , XYGubun , ELOW , EHIGH , VLOW , VHIGH , FASTIOCHK FROM MST_GrpTag B, MST_TAG_DCC C WHERE B.HOGI = C.IHOGI
AND B.ISEQ = C.ISEQ
AND B.YSEQ = C.ISEQ
AND B.GrpHOGI = #{hogi}
AND B.Gubun = #{dive}
AND B.ID = #{grpID}
AND B.MenuNo = #{menuNo}
AND B.GrpNo = #{uGrpNo}

	SELECT FLDNO 
	 FROM MST_TAG_DCC 
    WHERE IHOGI = #{Hogi}
       AND ADDRESS = #{ADDRESS}
       AND IOTYPE IN ('FTAI','FTDT')

	SELECT Convert(varchar,ScanTime,120) as SCANTIME
	 FROM LOG_Timer
    WHERE GUBUN = 'D'
       AND HOGI = #{hogi}
        AND DATATYPE = #{xyGubun}

        	SELECT C.TBLNO, C.FLDNO 
        	 FROM MST_GRPTAG B, MST_TAG_DCC C 
        	WHERE B.HOGI = C.IHOGI
			
           			AND B.ISEQ = C.ISEQ
           	
            
           			AND B.YSEQ = C.ISEQ
           	  
            
            		AND B.GrpHOGI = #{hogi}
            
         	
           			AND B.Gubun = #{dive}
       		
       		
           			AND B.ID = #{grpID}
       		
       		
           			AND B.MenuNo = #{menuNo}
       		      
       		
           			AND B.GrpNo = #{uGrpNo}
       				                          		
        	ORDER BY B.TAGNO

        SELECT * 
         FROM LOG_${hogi}DCC_TREND
    	 WHERE Scantime = #{pSCanTime}
    	  AND Seq in (
    						SELECT C.TBLNO 
    						 FROM MST_GrpTag B, MST_TAG_DCC C
     					   WHERE B.HOGI = C.IHOGI 
     					     
		           					AND B.YSEQ = C.ISEQ
				           	  
				            
				            		AND B.GrpHOGI = #{hogi}
				            
				         	
				           			AND B.Gubun = #{dive}
				       		
				       		
				           			AND B.ID = #{grpID}
				       		
				       		
				           			AND B.MenuNo = #{menuNo}
				       		      
				       		
				           			AND B.GrpNo = #{uGrpNo}
				       				             
     					)

      	SELECT ${tblnoGrp} SEQ, * 
      	 FROM LOG_${hogi}DCC_TREND${tblnoGrp}
      	  
	      	 WHERE WHERE Scantime >  DATEADD(SECOND, -5,#{pSCanTime}) AND Scantime <= #{pSCanTime}
      	 
      	 
	      	 WHERE WHERE Scantime = #{pSCanTime}

SELECT IHOGI, XYGUBUN, DESCR, IOTYPE, ADDRESS, IOBIT, ISEQ, LOOPNAME FROM MST_TAG_DCC WHERE IHOGI = #{txtHogi} AND ADDRESS = #{txtAddress}
AND IOTYPE = #{txtIoType}
AND IOBIT = #{txtIoBit}

    	SELECT
			CONVERT(VARCHAR(10),ISNULL(a.ISEQ,''))+'_'+CONVERT(VARCHAR(10),ISNULL(b.ISEQ,'')) AS SEQS
		FROM MST_TAG_DCC a, MST_TAG_DCC b
		WHERE a.IHOGI = b.IHOGI
		AND a.IOTYPE = b.IOTYPE
		AND a.ADDRESS = b.ADDRESS
		
		AND a.IOBIT = b.IOBIT
		
		AND a.IHOGI = #{txtHogi}
		AND a.IOTYPE = #{txtIoType}
		AND a.ADDRESS = #{txtAddress}
		
		AND a.IOBIT = #{txtIoBit}
		
		AND a.XYGUBUN = 'X'
		AND b.XYGUBUN = 'Y'

mapper.update:
    	UPDATE MST_GRPTAG SET
		Hogi = #{txtHogi}
		
			,ISEQ = #{iSeq}
			,YSEQ = #{ySeq}
		
		
			
				,ISEQ = #{iSeq}
			
			
				,YSEQ = #{ySeq}
			
		
		WHERE GUBUN = #{gubun}
		AND ID = #{grpId}
		AND MENUNO = #{menuNo}
		AND GRPNO = #{grpNo}
		AND GrpHOGI = #{hogi}
		AND TAGNO = #{tagNo}
