mapper.select:
SELECT SIGNAL_NAME, UNIT, GAIN, OFFSET, IOTYPE, REGISTER, IOBIT, TBLNO, FLDNO, BSCAL, C.ISEQ ISEQ, Signal_Desc, D0, D1, SaveCoreChk, UNIT_DIV, MinVal, MaxVal, C.HOGI, DESCR FROM MST_GrpTag B, MST_TAG_MARK C WHERE B.ISEQ = C.ISEQ AND B.HOGI = C.HOGI
AND B.Gubun = #{dive}
AND B.GrpHOGI = #{hogi}
AND B.ID = #{grpID}
AND B.MenuNo = #{menuNo}
AND B.GrpNo = #{uGrpNo}

 	SELECT C.TBLNO, C.FLDNO 
 	 FROM MST_GrpTag B, MST_TAG_MARK C
 	WHERE B.HOGI = C.HOGI
			AND B.ISEQ = C.ISEQ
  	
    			AND B.Gubun = #{dive}
		
     
     		AND B.GrpHOGI = #{hogi}
     
		
    			AND B.ID = #{grpID}
		
		
    			AND B.MenuNo = #{menuNo}
		      
		
    			AND B.GrpNo = #{uGrpNo}
				                          		
 	ORDER BY B.TAGNO

        SELECT * 
         FROM LOG_${hogi}MARK_TREND   
    	 WHERE Scantime = #{pSCanTime}
    	  AND Seq in (
    						SELECT C.TBLNO 
    						 FROM MST_GrpTag B, MST_TAG_MARK C
     					    WHERE B.HOGI = C.HOGI 
     					      AND B.ISEQ = C.ISEQ 
				            
				            		AND B.GrpHOGI = #{hogi}
				            
				         	
				           			AND B.Gubun = #{dive}
				       		
				       		
				           			AND B.ID = #{grpID}
				       		
				       		
				           			AND B.MenuNo = #{menuNo}
				       		      
				       		
				           			AND B.GrpNo = #{uGrpNo}
				       				             
     					)
